import React, { useState } from 'react';


import "./darkmode-btn.css";

const DarkmodeBtn = ({ label, toggled, onClick }) => {

  const [isToggled, setToggle] = useState(toggled);

  const darkModeHandler = () => {
    setToggle(!isToggled);
    onClick(!isToggled);

    isToggled ? document.getElementById("darkmode-btn").style.backgroundImage = "../../images/sunOn-moonOff.png" : 
    document.getElementById("darkmode-btn").style.backgroundImage = "../../images/moonOn-sunOff.png" 
  }

  return(
    <div id="darkmode-btn">

      <label>
        <input 
          type="checkbox" 
          defaultChecked={isToggled} 
          onClick={darkModeHandler} 
        />
        <span />
      {/* <strong> {label}</strong> */}
    </label>
   </div>
  )
};

export default DarkmodeBtn;
