import React, { useContext, useEffect, useState } from 'react';

import NaviButton from './components/naviButton';
import TrashButton from './components/trashButton';

import SaveButton from './components/saveButton';
import { MarkdownContext } from '../../context/markdownContext';
import './headerNavi.css';


const HeaderNavi = () => {

  const { sideMenuState } = useContext(MarkdownContext);
  const [ docPosition, setDocPosition ] = useState('')
  const closed = '250px';
  const open = '0px';
  let browserWidth = window.innerWidth;

  setDocPosition(browserWidth > 1249 ? '266px' : '80px');

  useEffect(() => {
    //Update the position of p#doc-title
    setDocPosition(browserWidth > 1249 ? '266px' : '80px');

  }, [browserWidth])



  return (
    <div 
      id='header-container'
      style={{left: `${!sideMenuState ? open : closed}`}}
    >
      <div id='header-menu'>
        <NaviButton />
        {/* <h3 id='title' >Markdown</h3> */}
        {browserWidth > 1249 ? <h3 id='title' >Markdown</h3> : <></>}
        <p id='doc-title' style={{left: docPosition}}>Title of Doc</p>
        <TrashButton />
        <SaveButton />
      </div>
    </div>
  )
}

export default HeaderNavi;
