import React, { useContext } from 'react';
import { MarkdownContext } from '../../context/markdownContext';
// import sun from '../../images/sunOn-moonOff.png';
// import moon from '../../images/moonOn-sunOff.png';
// import '../../images/moonOn-sunOff.png';

import '../darkmode/darkmode.css';
import './darkmode-btn.css';

const DarkmodeBtn = ({ onClick }) => {

  const {isToggled, setToggle} = useContext(MarkdownContext);

  // const bgHandler = () => {
  //   // !isToggled ? setSunMoonBG(moon) : setSunMoonBG(sun);

  //   const setBackground = (img) => document.getElementById('darkmode-btn').style.backgroundImage = `url(${img})`;

  //   !isToggled ? setBackground(sun) : setBackground(moon);
  // }

  const blockquoteHandler = () => {
    const tag = (tagName) => document.getElementsByTagName(tagName)[0];

    let blockText = tag('blockquote');

    if (!isToggled) {
      //lightmode
      blockText.style.backgroundColor = 'white';
      blockText.getElementsByTagName('p')[0].style.color = '#35393F';
      tag('a').color = '#35393F';

    } else {
      // darkmode
      blockText.style.backgroundColor = '#979797';
      blockText.getElementsByTagName('p')[0].style.color = 'white';
      tag('a').style.color = 'white';
    }
  }

  const darkModeHandler = () => {

    setToggle(!isToggled);
    onClick(!isToggled);

    // bgHandler();
    blockquoteHandler()
  }

  return(
    <div 
      id='darkmode-btn' 
      className={!isToggled ? 'sunBG' : 'moonBG'}
    >

      <label>
        <input 
          type='checkbox' 
          defaultChecked={isToggled} 
          onClick={darkModeHandler} 
        />
        <span />
    </label>
   </div>
  )
};

export default DarkmodeBtn;
