import { MarkdownContext } from '../../../context/markdownContext';

import './stylesheets/previewButton.css';


const PreviewButton = () => {
  let { previewOn, setPreviewOn } = useContext(MarkdownContext)

  const previewHandler = () => {
    if (previewOn) {
      setPreviewOn(false);
      // go to markdown
    } else {
      setPreviewOn(true);
      // go to preview
    }
  };

  return(
