import React, { useState } from 'react';

import sun from "../../images/sunOn-moonOff.png";
import moon from "../../images/moonOn-sunOff.png";
// import "../../images/moonOn-sunOff.png";
import "./darkmode-btn.css";

const DarkmodeBtn = ({ label, toggled, onClick }) => {

  const [isToggled, setToggle] = useState(toggled);

  let sunMoonBG = sun;

  const bgHandler = () => {
    !isToggled ? sunMoonBG = moon : sunMoonBG = sun;
    // if (!isToggled) {
    //   document.getElementById("darkMmode-btn").style.backgroundImage = "url('../../images/moonOn-sunOff.png')";
    // } else {
    //   document.getElementById("darkmode-btn").style.backgroundImage = "url('../../images/sunOn-moonOff.png')"
    // }
  }

  const darkModeHandler = (event) => {

    setToggle(!isToggled);
    onClick(!isToggled);

    event.target.style.backgroundImage = sun;

    // bgHandler();
  }

  return(
    <div id="darkmode-btn" >

      <label>
        <input 
          type="checkbox" 
          defaultChecked={isToggled} 
          onClick={(e) => {
            darkModeHandler(e)
          }} 
        />
        <span />
      {/* <strong> {label}</strong> */}
    </label>
   </div>
  )
};

export default DarkmodeBtn;
