import React, { useState } from 'react';

import sun from "../../images/sunOn-moonOff.png";
import moon from "../../images/moonOn-sunOff.png";
// import "../../images/moonOn-sunOff.png";
import "./darkmode-btn.css";

const DarkmodeBtn = ({ toggled, onClick }) => {

  const [isToggled, setToggle] = useState(toggled);

  const bgHandler = () => {
    // !isToggled ? setSunMoonBG(moon) : setSunMoonBG(sun);

    const setBackground = (img) => document.getElementById("darkmode-btn").style.backgroundImage = `url(${img})`;

    !isToggled ? setBackground(sun) : setBackground(moon);
  }

  const darkModeHandler = (event) => {

    setToggle(!isToggled);
    onClick(!isToggled);

    // bgHandler();
  }

  return(
    <div 
      id="darkmode-btn" 
      className={!toggled ? "moonBG" : "sunBG"}
    >

      <label>
        <input 
          type="checkbox" 
          defaultChecked={isToggled} 
          onClick={darkModeHandler} 
        />
        <span />
      {/* <strong> {label}</strong> */}
    </label>
   </div>
  )
};

export default DarkmodeBtn;
