import React, { useState } from 'react';

import sun from "../../images/sunOn-moonOff.png";
import moon from "../../images/moonOn-sunOff.png";
// import "../../images/moonOn-sunOff.png";
import "./darkmode-btn.css";

const DarkmodeBtn = ({ label, toggled, onClick }) => {

  const [isToggled, setToggle] = useState(toggled);
  // const [sunMoonBG, setSunMoonBG] = useState(sun);

  let sunMoonBG = sun;

  const bgHandler = () => {
    // !isToggled ? setSunMoonBG(moon) : setSunMoonBG(sun);

    if (!isToggled) {
      document.getElementById("darkmode-btn").style.backgroundImage = `url(${sun})`;
      console.log('This is the sun')
    } else {
      document.getElementById("darkmode-btn").style.backgroundImage = `url(${moon})`;
      console.log('this is the moon')
    }
  }

  const darkModeHandler = (event) => {

    setToggle(!isToggled);
    onClick(!isToggled);

    bgHandler();

    // event.target.style.backgroundImage = sun;

    // bgHandler();
  }

  return(
    <div id="darkmode-btn" style={sunMoonBG}>

      <label>
        <input 
          type="checkbox" 
          defaultChecked={isToggled} 
          onClick={darkModeHandler} 
        />
        <span />
      {/* <strong> {label}</strong> */}
    </label>
   </div>
  )
};

export default DarkmodeBtn;
